import React from "react";
import { useState } from "react";

function App() {
  const [name, setName] = useState();
  const[value, setValue] = useState();


  function toDoItem(e){

    setName(e.target.value);

  }


    function add(event){

      setValue(name);
      event.preventDefault();

    }


  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <form onSubmit={add}>
        <input onChange={toDoItem}type="text" value={name}/>
        <button onClick={add} type="submit" >
          <span>Add</span>
        </button>
        </form>

      </div>
      <div>
        <ul>
          <li>{value}</li>
        </ul>
      </div>
    </div>
  );
}

export default App;
